import React, { Component } from 'react';
import Navigationbar from '../navigation';
import '@fortawesome/fontawesome-free/css/all.min.css';
import { Link } from 'react-router-dom';
import { Form, Button, Card, CardGroup} from 'react-bootstrap';
import axios from 'axios';
import backendServer from '../../backendServer';
import ReactPaginate from 'react-paginate';
import '../restaurant/pagination.css';
// import { getMainCourse } from './getMaincourse';

export class userOrders extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userOrders: [],
            tempUserOrders: []
        };
    }


    componentDidMount() {
        axios.get(`${backendServer}/yelp/order/${localStorage.getItem("user_id")}`)
        .then(res => {
            this.setState({ userOrders: res.data, tempUserOrders: res.data });
        });
    }




    render () {

        let filteredUsers = this.state.userOrders.filter(
            filter => filter.message.message != null)
        console.log(filteredUsers)
        let renderOrders;
        if(this.state.tempUserOrders) {
        // console.log(this.state.yourOrders);
            renderOrders = this.state.tempUserOrders.map(order => {
                return (
                    <div>
                        <p> OrderId: {order._id} </p>
                        <p> {order.message.message} </p>
                        <p> {order.date} {order.time} </p>
                        <Button> Chat </Button>
                        <hr />
                        <br/>
                        <br/>
                    </div>
                )
            })
        }

        return (
            <React.Fragment>
                <Navigationbar/>
                <div class="container">
                <div >
                    <center>
                    <h1 style={{margin: "10px", color:"red"}}> All messages </h1>
                    </center>
                    <hr />
                    {renderOrders}
                </div>         
                </div>

            </React.Fragment>
        )
    }

}
export default userOrders;
