import {GET_RESTORDER, ADD_ORDER, UPDATE_ORDERSTATUS} from '../actions/types';

const initialState = {
    user: [], status:''
}

export default function(state = initialState, action){
    console.log(action.payload);
    switch(action.type){
        case GET_RESTORDER:
            return {
                ...state,
                user: action.payload
            };
        case ADD_ORDER:
            return {
                ...state,
                status: action.payload
            };

        case UPDATE_ORDERSTATUS:
            return {
                ...state,
                status: action.payload
            }

        default:
            return state
    }
};
